mapper.select:
SELECT * FROM ( SELECT rownum RNUM, TB.* FROM ( SELECT a.MBER_ID "mberId", a.MBER_NM "mberNm", a.MBTLNUM "mbtlnum", b.GROUP_NM "groupNm", c.AUTHOR_NM "authorNm", e.CODE_NM "codeNm" FROM LETTNGNRLMBER a , LETTNAUTHORGROUPINFO b, LETTNAUTHORINFO c, LETTCCMMNDETAILCODE e WHERE a.GROUP_ID = b.GROUP_ID AND a.AUTHOR_CODE = c.AUTHOR_CODE AND a.MBER_STTUS = e.CODE
and a.MBER_ID LIKE '%'|| #{searchKeyword} || '%'
and a.MBER_NM LIKE '%'|| #{searchKeyword} || '%'
and a.MBER_STTUS = #{mber_Sttus}
ORDER BY a.SBSCRB_DE DESC ) TB ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

select MBER_ID, PASSWORD, MBER_NM, MBER_STTUS, MBTLNUM, GROUP_ID, MBER_EMAIL_ADRES, SBSCRB_DE, ROLE_CODE, AUTHOR_CODE, CENTER_ID
from LETTNGNRLMBER
where MBER_ID = #{mberId}

            SELECT  NVL(count(*),0)      
		    FROM   LETTNGNRLMBER
		    WHERE  MBER_ID = #{mberId}

mapper.update:
update LETTNGNRLMBER set MBER_NM = #{mberNm}
                                            ,  MBER_STTUS = #{mberSttus}
                                            ,  MBTLNUM = #{mbtlnum, jdbcType=VARCHAR }
                                            ,  GROUP_ID = #{groupId}
                                            ,  MBER_EMAIL_ADRES = #{mberEmailAdres, jdbcType=VARCHAR }
                                            ,  ROLE_CODE = #{roleCode , jdbcType=VARCHAR }
                                            ,  AUTHOR_CODE = #{authorCode}
                                            ,  CENTER_ID = #{centerId , jdbcType=VARCHAR}                                                  

                                            , PASSWORD = #{password}

where MBER_ID = #{mberId}

mapper.select:
select NVL(count(*),0)
from LETTNGNRLMBER
where 
	MBER_ID = #{mberId}
	and MBER_NM = #{mberNm}
	and GROUP_ID = #{groupId , jdbcType=VARCHAR}
	and CENTER_ID = #{centerId , jdbcType=VARCHAR}
