mapper.select:
SELECT c.CENTER_NM, a.PLAY_DAY, b.REAL_FILE_NM as orignlFileNm, b.FILE_ALBUM, b.SINGER_NM, a.PLAY_CNT FROM TB_BASICFILEGROUP_PLAYINFO a , LETTNFILEDETAIL b, TB_CENTERINFO c WHERE a.ATCH_FILE_ID = b.ATCH_FILE_ID AND a.CENTER_ID = c.CENTER_ID
AND a.PLAY_DAY between #{searchStartDay} and #{searchEndDay}
AND a.CENTER_ID in
#{item}
order by c.CENTER_NM asc , a.PLAY_DAY asc

mapper.update:
UPDATE TB_PROGRAMUPDATEINFO set PROG_TITLE = #{progTitle}, FILE_INFO = #{fileInfo},
                                PROG_OSTYPE = #{progOstype},
                                LAST_UPDT_PNTTM = sysdate,
                                LAST_UPDUSR_ID = #{userId}
WHERE PROG_CODE = #{progCode}
