mapper.select:
SELECT * FROM ( SELECT rownum RNUM, TB.* FROM (
SELECT X.* FROM (
select a.ATCH_FILE_ID, substr(a.FILE_STRE_COURS, INSTR(a.FILE_STRE_COURS, '/',-1,3)) AS FILE_STRE_COURS, a.STRE_FILE_NM, a.FILE_EXTSN, a.CON_SEQ, a.FILE_ORDER, a.FILE_THUMNAIL , a.ORIGNL_FILE_NM , CASE WHEN a.FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN a.FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN a.FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType , a.PLAY_TIME, a.FILE_WIDTH , a.FILE_HEIGHT, a.UseYN, a.FILE_GUBUN from LETTNFILEDETAIL a where 1=1
and STRE_FILE_NM LIKE '%' || #{searchKeyword} || '%'
and ORIGNL_FILE_NM LIKE '%' || #{searchKeyword} || '%'
and a.FILE_GUBUN = #{fileGubun}
ORDER BY a.ATCH_FILE_ID DESC )X where 1=1
and X.mediaType = #{mediaType}
and X.mediaType != #{notConType}
) TB ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

select NVL(count(*),0) FROM ( SELECT CASE WHEN FILE_EXTSN IN ('mp4', 'avi', 'mpeg') THEN 'MEDIA' WHEN FILE_EXTSN IN ('jpg', 'jpeg', 'gif', 'png', 'bmp') THEN 'IMAGE' WHEN FILE_EXTSN IN ('mp3', 'wav', 'mid') THEN 'MUSIC' END AS mediaType, FILE_GUBUN FROM LETTNFILEDETAIL where 1=1
and STRE_FILE_NM LIKE '%' || #{searchKeyword} || '%'
and ORIGNL_FILE_NM LIKE '%' || #{searchKeyword} || '%'
and FILE_GUBUN = #{fileGubun}
) X where 1=1
and X.mediaType = #{mediaType}
and X.mediaType != #{notConType}

mapper.update:
update LETTNFILEDETAIL set PLAY_TIME = #{playTime,jdbcType=VARCHAR},
                                           FILE_WIDTH = #{fileWidth,jdbcType=VARCHAR}, 
                                           FILE_HEIGHT =  #{fileHeight,jdbcType=VARCHAR} 
where ATCH_FILE_ID = #{atchFileId}
