)

mapper.update:
UPDATE TB_CENTERINFO SET CENTER_NM = #{centerNm} , CENTER_ZIPCODE = #{centerZipcode} , CENTER_ADDR1 = #{centerAddr1} , CENTER_ADDR2 = #{centerAddr2} , CENTER_TEL = #{centerTel,jdbcType=VARCHAR} , CENTER_FAX = #{centerFax,jdbcType=VARCHAR} , CENTER_UPDATEID =#{centerUserId,jdbcType=VARCHAR} , CENTER_UPDATE_DT = SYSDATE , CENTER_URL = #{centerUrl ,jdbcType=VARCHAR} , CENTER_USEYN = #{centerUseYn,jdbcType=VARCHAR} , CENTER_LOUNGE = #{centerLounge,jdbcType=VARCHAR} , CENTER_MEETINGROOM = #{centerMeetingRoom,jdbcType=VARCHAR} , CENTER_STARTTIME = #{centerStartTime,jdbcType=VARCHAR} , CENTER_ENDTIME = #{centerEndTime,jdbcType=VARCHAR} , BROD_CODE = #{brodCode,jdbcType=VARCHAR} , CENTER_INFO = #{centerInfo,jdbcType=CLOB}
