SELECT X.didId, X.didNm, X.groupNm, NVL(X.schName, '없음') AS schName, X.didSttus, X.codeNm, X.RNUM FROM ( SELECT rownum RNUM, TB.* FROM ( SELECT a.DID_ID AS didId, a.DID_NM AS didNm, b.GROUP_NM AS groupNm, (SELECT C.SCH_NAME FROM TB_SCHEDULE C, TB_GROUPDID g , TB_CONTENTSENDHISTORY f WHERE TO_CHAR(SYSDATE, 'yyyyMMdd') BETWEEN c.SCH_STARTDAY AND c.SCH_ENDDAY AND c.GROUP_CODE = e.GROUP_CODE AND c.GROUP_CODE = g.GROUP_CODE AND c.sch_code = f.SCH_CODE AND f.DID_ID = a.DID_ID
AND c.SCH_CODE = #{searchschCode}
AND ROWNUM = 1 AND f.DID_UPDATECHECK = 'Y' ) AS schName, a.DID_STTUS AS didSttus, a.DID_MAC as didMac, EMARTCMS.FN_DETAILCODENM( a.DID_SWVER) AS codeNm FROM TB_DIDINFO a, TB_GROUP b, TB_GROUPDID e WHERE a.DID_ID = e.DID_ID AND b.GROUP_CODE = e.GROUP_CODE
AND a.DID_SWVER = #{searchdidSwver}
and A.DID_MODELTYPE = 'DIDRMODELTYPE03'
and a.ROLE_CODE in (SELECT GROUP_ID FROM LETTNAUTHORGROUPINFO WHERE parent_group_id = #{groupCode} )
and a.ROLE_CODE = #{groupCode}
ORDER BY a.FRST_REGIST_PNTTM DESC ) TB ) X WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


         SELECT NVL(COUNT(*),0) 
         FROM TB_DIDINFO a, TB_GROUP b, TB_GROUPDID e           
