SELECT * FROM ( SELECT rownum RNUM, TB.* FROM ( SELECT A.DID_ID as "didId", A.DID_NM as "didNm", A.DID_IPADDR as "didIpaddr", B.CENTER_NM as "centerNm", CASE A.DID_STTUS WHEN 'N' THEN 'OFF' ELSE 'ON' END AS didSttus, A.DID_USEYN as "didUseYn", EMARTCMS.fn_DetailCodeNm(A.DID_OS) as didOs, a.DID_MAC as didMac,
(SELECT COUNT(*) FROM TB_SCHEDULE WHERE GROUP_CODE=(SELECT group_code FROM TB_GROUPDID WHERE did_id=a.did_id) AND TO_CHAR(SYSDATE, 'YYYYMMDD') <= SCH_ENDDAY ) AS schCnt
FROM TB_DIDINFO a, TB_CENTERINFO b WHERE a.CENTER_ID = B.CENTER_ID
and a.DID_ID LIKE '%'|| #{searchKeyword} || '%'
and a.DID_NM LIKE '%'|| #{searchKeyword} || '%'
and A.DID_MODELTYPE = 'DIDRMODELTYPE03'
and a.ROLE_CODE in (SELECT GROUP_ID FROM LETTNAUTHORGROUPINFO WHERE parent_group_id = #{groupCode} )
and a.ROLE_CODE = #{groupCode}
AND a.CENTER_ID = #{centerId}
AND a.DID_MODELTYPE = #{didModelType}
ORDER BY a.FRST_REGIST_PNTTM DESC ) TB ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT a.DID_ID as didId , a.DID_NM as didNm, a.DID_MAC as didMac, a.DID_IPADDR as didIpaddr, fn_DetailCodeNm(a.DID_SWVER) as didSwver, b.CENTER_NM as centerNM, fn_DetailCodeNm( a.DID_TYPE) as didType, fn_DetailCodeNm( a.DID_RESOLUTION ) as didResolution, DID_WIDTH as didWidth, DID_HEIGHT as didHeight, fn_DetailCodeNm( a.DID_IPTYPE) as didIptype, fn_DetailCodeNm(a.DID_MODELTYPE) as didModelType, DID_USEYN as didUseYn, TO_CHAR(DID_ENDCONTIME, 'YYYY-MM-DD HH24:MI:SS') as didEndContime, DID_ENDTIME as didEndTime , DID_REMARK as didRemark, c.GROUP_NM as roleNm, DID_STTUS as didSttus, fn_DetailCodeNm(a.DID_OS) as didOs, fn_DetailCodeNm(a.DID_SERIALTYPE) as didSerialtype, fn_DetailCodeNm(a.DID_SERIALPORT) as didSerialport, fn_DetailCodeNm(a.DID_MONITERCNT) as didMonitercnt, DID_STAETTIME as didStartTime, DID_TIMEINTERVAL as didTimeInterval, DID_SERIALJAVASCRIPT as didSerialjavascript, (SELECT e.GROUP_NM FROM TB_GROUPDID d, TB_GROUP e WHERE d.group_code=e.GROUP_CODE AND d.DID_ID=a.DID_ID) AS groupNm, (SELECT e.GROUP_CODE FROM TB_GROUPDID d, TB_GROUP e WHERE d.group_code=e.GROUP_CODE AND d.DID_ID=a.DID_ID) AS groupCode,
(SELECT COUNT(*) FROM TB_SCHEDULE WHERE GROUP_CODE=(SELECT group_code FROM TB_GROUPDID WHERE did_id=a.did_id) AND TO_CHAR(SYSDATE, 'YYYYMMDD') <= SCH_ENDDAY ) AS schCnt
FROM TB_DIDINFO a, TB_CENTERINFO b, LETTNAUTHORGROUPINFO c WHERE a.CENTER_ID = b.CENTER_ID AND a.ROLE_CODE = c.GROUP_ID AND a.DID_ID = #{didId}

select NVL(DID_MAC, 'N')
